<?php

namespace App\Observers;

use App\Enums\PackageType;
use App\Models\Apartment;
use App\Models\Currency;
use App\Models\GlobalCurrency;
use App\Models\GlobalInvoice;
use App\Models\GlobalSubscription;
use App\Models\Maintenance;
use App\Models\NotificationSetting;
use App\Models\Package;
use App\Models\PaymentGatewayCredential;
use App\Models\Society;
use App\Models\GlobalSetting;
use App\Models\ModuleSetting;
use App\Models\ServiceType;
use App\Events\NewSocietyCreatedEvent;
use App\Models\OnboardingStep;

class SocietyObserver
{
    public function saving(Society $society): void
    {
        if ($society->isDirty('name')) {
            $society->slug = $this->createUniqueSlug($society);
            $society->hash = md5(microtime());
        }

        if ($society->isDirty(['package_id', 'package_type', 'license_expire_on'])) {
            clearSocietyModulesCache($society->id);
        }
    }

    public function created(Society $society)
    {
        // Add Payment Gateway Settings
        PaymentGatewayCredential::firstOrCreate(['society_id' => $society->id]);

        // Add Currencies
        $this->addCurrencies($society);

        // Add Notification Settings
        $this->addNotificationSettings($society);

        // Create Subscription
        $this->createSubscription($society);

        // Add Apartments
        $this->addApartments($society);

        // Add Maintenance
        $this->addMaintenance($society);

        // Add Module Settings
        $this->addModuleSettings($society);

        // Add Service Types
        $this->addServiceType($society);

        OnboardingStep::create(['society_id' => $society->id]);

        // Will be used in various module
        event(new NewSocietyCreatedEvent($society));
    }

    private function addApartments(Society $society)
    {
        $apartmentTypes = [
            ['apartment_type' => '1 BHK', 'maintenance_value' => 100],
            ['apartment_type' => '2 BHK', 'maintenance_value' => 200],
            ['apartment_type' => '3 BHK', 'maintenance_value' => 300],
            ['apartment_type' => '4 BHK', 'maintenance_value' => 400],
            ['apartment_type' => '5 BHK', 'maintenance_value' => 500],
        ];

        foreach ($apartmentTypes as $type) {
            Apartment::create([
                'apartment_type' => $type['apartment_type'],
                'maintenance_value' => $type['maintenance_value'],
                'society_id' => $society->id,
            ]);
        }
    }

    private function addMaintenance(Society $society)
    {
        Maintenance::create([
            'set_value' => rand(500, 5000),
            'unit_name' => 'sqft',
            'society_id' => $society->id,
        ]);
    }

    private function createSubscription(Society $society)
    {
        // Check if a trial package exists and trial status is active (1 or true)
        $trialPackage = Package::firstWhere('package_type', 'trial');
        $isTrialActive = $trialPackage && $trialPackage->trial_status == 1;

        // Assign either trial package or default package
        $package = $isTrialActive ? $trialPackage : Package::firstWhere('package_type', PackageType::DEFAULT)->first();

        if (is_integer($trialPackage->trial_days)) {
            $trialDays = $trialPackage->trial_days;
        } else {
            $trialDays = null;
        }
        // Update society package details
        $society->update([
            'package_id' => $package->id,
            'package_type' => $isTrialActive ? 'trial' : 'monthly',
            'trial_ends_at' => $isTrialActive ? now()->addDays($trialDays) : null,
            'license_expire_on' => $isTrialActive ? now()->addDays($trialDays) : now()->addMonth(),
            'license_updated_at' => now(),
            'subscription_updated_at' => now(),
        ]);

        // Create subscription
        $subscription = GlobalSubscription::create([
            'society_id' => $society->id,
            'package_id' => $society->package_id,
            'currency_id' => $package->currency_id,
            'package_type' => $society->package_type,
            'quantity' => 1,
            'gateway_name' => 'offline',
            'subscription_status' => 'active',
            'trial_ends_at' => $isTrialActive ? $society->license_expire_on : null,
            'subscribed_on_date' => $society->license_updated_at,
            'ends_at' => $society->license_expire_on,
            'transaction_id' => strtoupper(str()->random(15)),
        ]);

        // Create invoice
        GlobalInvoice::create([
            'society_id' => $society->id,
            'global_subscription_id' => $subscription->id,
            'package_id' => $subscription->package_id,
            'currency_id' => $subscription->currency_id,
            'offline_method_id' => null,
            'package_type' => $subscription->package_type,
            'total' => 0,
            'gateway_name' => 'offline',
            'status' => 'active',
            'pay_date' => $subscription->subscribed_on_date,
            'next_pay_date' => $subscription->ends_at,
            'transaction_id' => $subscription->transaction_id,
        ]);

        clearSocietyModulesCache($society->id);
    }


    public function addCurrencies($society)
    {
        $globalCurrencies = GlobalCurrency::get();

        foreach ($globalCurrencies as $globalCurrency) {
            $currency = new Currency();
            $currency->currency_name = $globalCurrency->currency_name;
            $currency->currency_symbol = $globalCurrency->currency_symbol;
            $currency->currency_code = $globalCurrency->currency_code;
            $currency->society_id = $society->id;
            $currency->saveQuietly();
        }

        $globalSetting = GlobalSetting::first();
        $defaultCurrency = Currency::where('currency_code', $globalSetting->defaultCurrency->currency_code)->where('society_id', $society->id)->first();

        if (!$defaultCurrency) {
            $defaultCurrency = Currency::create([
                'currency_name' => $globalSetting->defaultCurrency->currency_name,
                'currency_symbol' => $globalSetting->defaultCurrency->currency_symbol,
                'currency_code' => $globalSetting->defaultCurrency->currency_code,
                'society_id' => $society->id
            ]);
        }

        $society->currency_id = $defaultCurrency->id;
        $society->save();
    }

    public function addNotificationSettings($society)
    {
        $notificationTypes = [
            [
                'type' => 'maintenance',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'noticeboard',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'visitor_arrival',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'ticket',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'contract_end_date_reminder',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'rent_payment',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'rent_payment_due',
                'send_email' => 1,
                'society_id' => $society->id
            ],
            [
                'type' => 'amenitites',
                'send_email' => 1,
                'society_id' => $society->id
            ],
        ];

        NotificationSetting::insert($notificationTypes);
    }

    private function addModuleSettings(Society $society)
    {
        $modules = [
            'Tower',
            'Floor',
            'Apartment',
            'User',
            'Owner',
            'Tenant',
            'Rent',
            'Utility Bills',
            'Common Area Bills',
            'Maintenance',
            'Amenities',
            'Book Amenity',
            'Visitors',
            'Notice Board',
            'Tickets',
            'Parking',
            'Service Provider',
            'Service Time Logging',
            'Assets',
            'Event',
            'Forum',
        ];

        $types = ['Admin', 'Manager', 'Owner', 'Tenant', 'Guard'];

        $data = [];
        foreach ($types as $type) {
            foreach ($modules as $module) {
                $data[] = [
                    'society_id' => $society->id,
                    'module_name' => $module,
                    'status' => 'active',
                    'type' => $type,
                    'created_at' => now(),
                    'updated_at' => now(),
                ];
            }
        }

        ModuleSetting::insert($data);
    }

