        foreach ($services as $service) {
            ServiceType::create([
                'name' => $service['name'],
                'icon' => $service['icon'],
                'society_id' => $society->id,
            ]);
        }
    }

    private function createUniqueSlug($society)
    {
        $name = $society->name;
        $slug = str()->slug($name);

        // Check if slug already exists in the database
        $count = 0;
        $originalSlug = $slug;

        while (Society::where('slug', $slug)
            ->where('id', '<>', $society->id)
            ->exists()
        ) {
            // Append counter to make the slug unique
            $count++;
            $slug = "{$originalSlug}-{$count}";
        }

        return $slug;
    }
